xpath.script:
document.addEventListener('DOMContentLoaded', function () {
    // Seleccionar el elemento con el id "dynamic-text"
    var dynamicText = document.getElementById('dynamic-text');

    // Verificar que el elemento existe
    if (dynamicText) {
        // Agregar un mensaje dinámico debajo del texto estático
        var additionalMessage = document.createElement('p');
        additionalMessage.innerHTML = '¡Este es un mensaje adicional agregado con JavaScript!';

        // Agregar el mensaje debajo del texto estático
        dynamicText.parentNode.appendChild(additionalMessage);
    }
});
